mapper.select:
SELECT
*
from company

	user_id = #{userId,jdbcType=VARCHAR}
	and year = #{year,jdbcType=VARCHAR}
	and company_id = #{company_id,jdbcType=VARCHAR}

mapper.delete:
delete
from company

	user_id = #{userId,jdbcType=VARCHAR}
	and year = #{year,jdbcType=VARCHAR}
	and company_id = #{company_id,jdbcType=VARCHAR}
